import classes from './CartCheckOut.module.css';
import useNew from '../../hooks/input-cust';
function CartCheckOut(props){
    const {value:enteredName,validValue:enteredNameIsValid,hasError:nameHasError,newValueChangeHandler:newNameChangeHandler,inputBlurHandler:nameblurHandler,reset:nameReset}=useNew(value=>value.tr()!== 0);
    
    const submitHandler=(event)=>{
        event.preventDefault();

        if(!enteredNameIsValid){
            return;
        }
        console.log(enteredName);
        nameReset();
    };

    return(
    <form className={classes.form} onSubmit={submitHandler}>    
        <div className={classes.control}>
            <label htmlFor="name">Enter your name</label>
            <input type="Text" id="name" onChange={newNameChangeHandler} onBlur={nameblurHandler} value={enteredName}></input>
        </div>
        {nameHasError && <p>Please Enter Valid Name! </p>}
        <div className={classes.control}>
        <label htmlFor="address">Address</label>
        <input type="Text" id="address"></input>
        </div>
        <div className={classes.control}>
        <label htmlFor="pincode">Pincode</label>
        <input type="Text" id="pincode"></input>
        </div>
        <div className={classes.control}>
        <label htmlFor="number">Number</label>
        <input type="number" id="number"></input>
        </div>
        <div className={classes.actions}> <button>Confirm</button>
        
        <button type="button" onClick={props.toCancle}>Cancle</button>
        </div>
    </form>
    );
}

export default CartCheckOut;
