import classes from './MealItemForm.module.css';
import Input from '../../UI/Input';
import React,{useState} from 'react';
function MealItemForm(props){
const[enteredValue,setEnteredValue]=useState('');
const onInputChangeHandler=(event)=>{
    setEnteredValue(event.target.value);

}
    const onSubmitHandler = (event)=>{
     event.preventDefault();    
     props.onAddToCart(enteredValue);
     setEnteredValue('');
    }

    return <form className={classes.form} onSubmit={onSubmitHandler}>
        <input label="Amount"   type="number"  min= '1' max= '5' step='1' defaultValue='1' onChange={onInputChangeHandler} value={enteredValue} id={"amount" + props.id}></input>

        <button className={classes.button}>+ Add</button>
    </form>
}

export default MealItemForm;
