import React,{useContext} from 'react';
import classes from './Cart.module.css';
import Model from '../UI/Model';
import CartContext from '../../store/cart-context';

function Cart(props){
    const ctx=useContext(CartContext);
    const totalAmount = `Rs${ctx.totalAmount}`;
    const cartList=<ul className={classes['cart-items']}>{ctx.items.map(item=>
    <li>{item.name}</li>)}</ul>;
return(
    <Model onClose={props.onDisableCart}>
        {cartList}
        <div className={classes.total}>
        <span>Total Amount </span>
        <span> {totalAmount}</span>
        </div>
        <div className={classes.actions}>
            <button className={classes['button--alt']} onClick={props.onDisableCart}>Close</button>
            <button className={classes.button}>Order</button>

        </div>
    </Model>
);

}

export default Cart;
