import {useState} from "react";
function useNew(validate){
    const [newEnteredValue,setEnteredValue] = useState('');
    const [isTouched,setIstouched] = useState(false);

    const enterdValueIsValid = validate(newEnteredValue);
    const hasError  = !enterdValueIsValid && isTouched;

    function newValueChangeHandler(event){
        setEnteredValue(event.target.vaule);
    }

    function inputBlurHandler(){
        setIstouched(true);
    }

    function reset(){
        setEnteredValue('');
        setIstouched(false);
    }

    return ({
        value:newEnteredValue,
        validValue:enterdValueIsValid,
        hasError,
        reset,
        newValueChangeHandler,
        inputBlurHandler
    });

}

export default useNew;
